CREATE MATERIALIZED VIEW course_reg_offer_join AS SELECT courseid, courseoffers.courseofferid, year, quartile, studentid, courseregistrations.studentregistrationid, grade FROM CourseRegistrations INNER JOIN CourseOffers ON (CourseRegistrations.CourseOfferId = CourseOffers.CourseOfferId) INNER JOIN studentregistrationstodegrees ON (courseregistrations.studentregistrationid = studentregistrationstodegrees.studentregistrationid);
